mapper.select:
SELECT * FROM product

	WHERE productCategoryNum = #{productCategoryNum}

ORDER BY productCode DESC;

SELECT * FROM product INNER JOIN category ON product.productCategoryNum = category.productCategoryNum WHERE productName LIKE CONCAT('%', #{searchName}, '%') AND productCode LIKE CONCAT('%', #{searchCode}, '%')
AND product.productCategoryNum = #{productCategoryNum}
ORDER BY productCode DESC;

select * from product
where productName LIKE CONCAT('%',
#{searchName}, '%');
